var userChoice = prompt("Do you choose rock, paper or scissors?"); //Prompt user to enter either "rock" or "paper" or "scissors"

var computerChoice = Math.random();//Random number generator between 0 and 1 



/* check conditions to decide which choice the computer chooses....its all 
completely random! */

if(computerChoice >=0 && computerChoice <=0.33)
{
    computerChoice = "rock";
}
else if(computerChoice >= 0.34 && computerChoice <= 0.66)
{
    computerChoice = "paper";
}
else
{
    computerChoice = "scissors";
}

/* Function to compare values entered by the user and generated by the computer */
var compare = function(choice1, choice2){
    if(choice1 == choice2)
    {
        return ("The result is a tie!");
    }
    else if(choice1 == "rock")
    {
        if(choice2 == "scissors")
        {
            return ("rock wins");
        }
        else
        {
            return ("paper wins");
        }
    }
    else if(choice1 == "paper")
    {
        if(choice2 == "rock")
        {
            return ("paper wins");
        }
        else
        {
            return ("scissors wins");
        }
    }
    else if(choice1 == "scissors")
    {
        if(choice2 == "rock")
        {
            return ("rock wins");
        }
        else
        {
            return ("scissors wins");
        }
    }
};
var result = compare(userChoice, computerChoice); // Call the function using the user's choice and the computer's choice
console.log(result); //Log the result on console
